import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function QueueForm() {
  const [fullName, setFullName] = useState('');
  const [category, setCategory] = useState('grant');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await axios.post('/api/queue', {
      full_name: fullName,
      category: category,
    });

    setFullName('');
    setCategory('grant');

    navigate('/ticket', { state: { entry: response.data } }); // переход на печать
  };

  return (
    <div className="p-6 max-w-2xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">University Queue System</h1>

      <form onSubmit={handleSubmit} className="mb-6 space-y-4">
        <input
          type="text"
          placeholder="Full name"
          className="border p-2 w-full"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
          required
        />
        <select
          className="border p-2 w-full"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="grant">Bachelor (Grant)</option>
          <option value="paid">Bachelor (Paid)</option>
          <option value="magistracy">Magistracy</option>
          <option value="phd">PhD</option>
          <option value="platonus">Platonus</option>
        </select>
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
          Register
        </button>
      </form>
    </div>
  );
}
