import React, { useEffect, useState } from 'react';

function LastCallsPage() {
  const [calls, setCalls] = useState([]);

  useEffect(() => {
    const fetchCalls = () => {
      fetch('/api/queue/last-calls')
        .then(res => res.json())
        .then(data => setCalls(data));
    };
    fetchCalls();
    const interval = setInterval(fetchCalls, 5000); // автообновление каждые 5 сек
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-900 to-blue-600">
      <div className="w-[80vw] bg-white bg-opacity-95 rounded-xl shadow-2xl p-8 flex flex-col items-center">
        <h1 className="text-5xl font-extrabold text-blue-900 mb-8 tracking-widest drop-shadow-lg select-none w-full text-center">
          Табло вызова
        </h1>
        <table className="w-full text-2xl">
          <thead>
            <tr className="text-blue-900 border-b-2 border-blue-300">
              <th className="py-4">№</th>
              <th className="py-4">Время</th>
              <th className="py-4">ФИО</th>
              <th className="py-4">Стол</th>
            </tr>
          </thead>
          <tbody>
            {calls.length === 0 ? (
              <tr>
                <td colSpan={4} className="text-center text-gray-400 py-8 text-3xl">
                  Нет вызовов
                </td>
              </tr>
            ) : (
              calls.map((call) => (
                <tr key={call.id} className="hover:bg-blue-50 transition">
                  <td className="py-4 font-bold text-3xl text-blue-900 text-center">{call.number}</td>
                  <td className="py-4 text-center text-gray-700">
                    {new Date(call.timestamp).toLocaleTimeString('ru-RU')}
                  </td>
                  <td className="py-4 text-center text-gray-800 font-semibold">{call.full_name || '-'}</td>
                  <td className="py-4 text-center text-blue-700 font-bold">{call.desk || '-'}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default LastCallsPage;
