import React, { useState } from "react";

const getCategoryInfo = (number) => {
  if (number >= 1 && number <= 499) return { label: "Бакалавр (Грант)", color: "blue" };
  if (number >= 500 && number <= 599) return { label: "Бакалавр (Платное)", color: "green" };
  if (number >= 600 && number <= 699) return { label: "Магистратура", color: "purple" };
  if (number >= 700 && number <= 799) return { label: "PhD", color: "maroon" };
  if (number >= 800 && number <= 999) return { label: "Platonus", color: "gray" };
  return { label: "Неизвестно", color: "black" };
};

export default function QueueDisplay({ queue }) {
  const [filter, setFilter] = useState("all");

  const filteredQueue = queue.filter((entry) => {
    const deskNum = parseInt(entry.desk);
    if (filter === "all") return true;
    if (filter === "1-10") return deskNum >= 1 && deskNum <= 10;
    if (filter === "11-12") return deskNum === 11 || deskNum === 12;
    if (filter === "305-307") return [305, 306, 307].includes(deskNum);
    return true;
  });

  return (
    <div style={{ marginTop: 30 }}>
      <h2 style={{ fontSize: 20, fontWeight: "bold", marginBottom: 15 }}>Текущая очередь</h2>

      <div style={{ marginBottom: 15 }}>
        <label style={{ marginRight: 10 }}>Фильтр по столу:</label>
        <select value={filter} onChange={(e) => setFilter(e.target.value)}>
          <option value="all">Все столы</option>
          <option value="1-10">Столы 1–10</option>
          <option value="11-12">Столы 11–12</option>
          <option value="305-307">Каб. 305–307</option>
        </select>
      </div>

      {filteredQueue.length === 0 ? (
        <p style={{ color: "gray", fontStyle: "italic" }}>Нет абитуриентов в очереди</p>
      ) : (
        <table border="1" cellPadding="10" style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead style={{ backgroundColor: "#eee" }}>
            <tr>
              <th>Номер</th>
              <th>ФИО</th>
              <th>Стол</th>
              <th>Категория</th>
              <th>Статус</th>
            </tr>
          </thead>
          <tbody>
            {filteredQueue.map((entry, index) => {
              const { label, color } = getCategoryInfo(entry.number);
              return (
                <tr key={index}>
                  <td>{entry.number}</td>
                  <td>{entry.full_name}</td>
                  <td>{entry.desk || "–"}</td>
                  <td style={{ color }}>{label}</td>
                  <td>{entry.status === "called" ? "Вызван" : "Ожидает"}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      )}
    </div>
  );
}
