import React, { useEffect, useState } from 'react';

function LastCallsPage() {
  const [calls, setCalls] = useState([]);

  useEffect(() => {
    const fetchCalls = () => {
      fetch('/api/queue/last-calls')
        .then(res => res.json())
        .then(data => setCalls(data));
    };
    fetchCalls();
    const interval = setInterval(fetchCalls, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="w-screen h-screen flex flex-col items-center justify-center bg-gradient-to-br from-blue-900 to-blue-600 p-0 m-0">
      <h1 className="text-6xl font-extrabold text-center text-blue-100 mb-12 tracking-widest drop-shadow-lg select-none">Табло вызова</h1>
      <table className="w-11/12 md:w-4/5 lg:w-3/5 xl:w-1/2 text-3xl bg-white bg-opacity-90 rounded-2xl shadow-2xl mx-auto">
        <thead>
          <tr className="text-blue-700 border-b-4 border-blue-400">
            <th className="py-6 px-2">№</th>
            <th className="py-6 px-2">Время</th>
            <th className="py-6 px-2">ФИО</th>
            <th className="py-6 px-2">Стол</th>
          </tr>
        </thead>
        <tbody>
          {calls.length === 0 ? (
            <tr>
              <td colSpan={4} className="text-center text-gray-500 py-16 text-4xl">Нет вызовов</td>
            </tr>
          ) : (
            calls.map((call) => (
              <tr key={call.id} className="hover:bg-blue-50 transition">
                <td className="py-6 px-2 font-extrabold text-4xl text-blue-900 text-center">{call.number}</td>
                <td className="py-6 px-2 text-center text-gray-700">{new Date(call.timestamp).toLocaleTimeString('ru-RU')}</td>
                <td className="py-6 px-2 text-center text-gray-800 font-semibold">{call.full_name || '-'}</td>
                <td className="py-6 px-2 text-center text-blue-700 font-bold">{call.desk || '-'}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default LastCallsPage;
