import React, { useState } from 'react';
import axios from 'axios';

export default function CallNextForm() {
  const [category, setCategory] = useState('grant');
  const [desk, setDesk] = useState('');
  const [callResult, setCallResult] = useState(null);

  const handleCall = async () => {
    if (!desk || !category) {
      alert("Заполните категорию и номер стола");
      return;
    }

    try {
      const response = await axios.post('/api/queue/call', {
        category,
        desk,
      });
      setCallResult(response.data);
    } catch (err) {
      alert("Ошибка вызова: " + err.message);
    }
  };

  return (
    <div className="mb-4">
      <h2 className="h5 mb-3">Вызов следующего</h2>

      <div className="mb-3">
        <select
          className="form-select"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="grant">Bachelor (Grant)</option>
          <option value="paid">Bachelor (Paid)</option>
          <option value="magistracy">Magistracy</option>
          <option value="phd">PhD</option>
          <option value="platonus">Platonus</option>
        </select>
      </div>

      <div className="mb-3">
        <input
          type="number"
          placeholder="Номер стола"
          className="form-control"
          value={desk}
          onChange={(e) => setDesk(e.target.value)}
        />
      </div>

      <button
        onClick={handleCall}
        className="btn btn-success w-100"
      >
        Вызвать следующего ({category})
      </button>

      {callResult && (
        <div className={`alert mt-3 ${callResult.success ? 'alert-success' : 'alert-warning'}`}>
          {callResult.success
            ? `Вызван #${callResult.number} — ${callResult.full_name} (${callResult.category}) на стол ${callResult.desk}`
            : callResult.message}
        </div>
      )}
    </div>
  );
}
